    private void jbtn_enviar_depositoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtn_enviar_depositoActionPerformed
        try {
            if(this.jrdb_depo_corriente.isSelected() || this.jrdb_depo_ahorro.isSelected()){
                if (this.jrdb_depo_corriente.isSelected() && Util.lista.get(Util.usuarioConectado()).isTieneCorriente()) {
                    if (Integer.parseInt(jtxt_monto_deposito.getText()) > 0) {
                        Util.lista.get(Util.usuarioConectado()).setSaldoCorriente(Util.lista.get(Util.usuarioConectado()).getSaldoCorriente() + Integer.parseInt(jtxt_monto_deposito.getText()));
                        JOptionPane.showMessageDialog(this, "Su nuevo saldo de cuenta corriente es : "+Util.lista.get(Util.usuarioConectado()).getSaldoCorriente(), "Operacion Realizada", 1);                        
                    } else {
                        JOptionPane.showMessageDialog(this, "El deposito debe ser mayor a 0", "Error", 0);
                    }
                } else if(this.jrdb_depo_ahorro.isSelected() && Util.lista.get(Util.usuarioConectado()).isTieneAhorro()){
                    if (Integer.parseInt(jtxt_monto_deposito.getText()) > 0) {
                        Util.lista.get(Util.usuarioConectado()).setSaldoAhorro(Util.lista.get(Util.usuarioConectado()).getSaldoAhorro() + Integer.parseInt(jtxt_monto_deposito.getText()));
                        JOptionPane.showMessageDialog(this, "Su nuevo saldo de cuenta Ahorro es : "+Util.lista.get(Util.usuarioConectado()).getSaldoAhorro(), "Operacion Realizada", 1);
                       
                    } else {
                        JOptionPane.showMessageDialog(this, "El deposito debe ser mayor a 0", "Error", 0);
                    }
                }
            } else {
                JOptionPane.showMessageDialog(this, "No selecciono cuenta", "Error", 0);
            }
            
        } catch (Exception e) {
            System.out.println("Error depositoActionPerformed: "+e.getMessage());
        }
    }//GEN-LAST:event_jbtn_enviar_depositoActionPerformed

